import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, ImageBackground, Button } from 'react-native'
import Square from './Square.js'
import { connect } from 'react-redux'

const GameBoard = (props) => {
  const [timeLeft, setTimeLeft] = useState(60)

  useEffect(() => {
    if(!timeLeft) return
    const timerId = setInterval(() => {
      //happens every 1000ms
      setTimeLeft(timeLeft -1)
    },1000)
    return () => clearInterval(timerId)
  }, [timeLeft])

    return (
        <ImageBackground 
        style={styles.container}
        source={require('../assets/background.png')}
        >
        <Text style={styles.header}>Köstebekleri Yakala!</Text>
        <Text>{timeLeft} saniyen kaldi</Text>
        <Text>{props.score} tane köstebek yakalandi!</Text>
        <View style={styles.game}>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Square></Square>
          <Button
            title="Yeni Oyun"
            onPress={() => timerId? props.timerId : null}
            color="#2097F3"
            style={timerId? styles.buttonDisplay : styles.buttonNone}
          />
          <Text style={styles.footer}>Gelistiren: Firat Goktepe</Text>
        </View>
      </ImageBackground>

    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
    },
    game: {
      flexDirection: 'row',
      flexWrap: 'wrap',
      width: 300,
      paddingTop: 20,
    },
    header: {
      fontWeight: 'bold',
      marginBottom: 10,
      marginTop: 100
    },
    footer: {
      fontStyle: 'italic',
      marginTop: 200,
      marginLeft: 70
    },
    buttonDisplay: {
      display: 'inline-flex'
    },
    buttonNone: {
      display: 'none'
    }
  });

  const mapStateToProps = state => {
    return {
      score: state.score
    }
  }

export default connect(mapStateToProps)(GameBoard)
